<!DOCTYPE html>
<html>
  <head>
    <meta name="viewport" content="initial-scale=1.0, user-scalable=no">
    <meta charset="utf-8">
    <title>Google Maps JavaScript API v3 Example: Geocoding Simple</title>
    
    <script type="text/javascript" src="http://code.jquery.com/jquery-1.9.1.js"></script>
    <script type="text/javascript" src="https://maps.googleapis.com/maps/api/js?v=3.exp&sensor=false"></script>
    <script>
      var base_url = '<?php echo base_url();?>';
      var map;
      var geocoder;
      var bounds;
      var markersArr = [];
      
      function initialize() {
		geocoder = new google.maps.Geocoder();
		bounds = new google.maps.LatLngBounds();
		
        var latlng = new google.maps.LatLng(37.090240, -95.712891);
        var mapOptions = {
          zoom: 4,
          center: latlng,
          mapTypeId: google.maps.MapTypeId.ROADMAP
        }
        
        map = new google.maps.Map(document.getElementById('map_canvas'), mapOptions);
        
        getAllAddedLocations();
      }

		var marker;
      function codeAddress(fieldsType) {
		var getAddressVal = '';
		switch(fieldsType){
			case "country": 
				getAddressVal = $("#country_name option:selected").text();
			break;
			case "state": 
				getAddressVal = $("#state_name option:selected").text();
			break;
			case "city": 
				getAddressVal = $("#city_name option:selected").text();
			break;

			default: 
				getAddressVal = $('input#location_name').val();
			break;
		}
		
		if(getAddressVal == ''){
			$('input#location_name').focus();
			return false;
		}
		
		if(marker){
			marker.setMap(null);  
		}
        
        geocoder.geocode( { 'address': getAddressVal}, function(results, status) {
          if (status == google.maps.GeocoderStatus.OK) {
            map.setCenter(results[0].geometry.location);
            marker = new google.maps.Marker({
                map: map,
				draggable:true,
                position: results[0].geometry.location
            });
			
			var lat = results[0].geometry.location.lat();
			var lng = results[0].geometry.location.lng();
			
			google.maps.event.addListener(marker, 'dragend', function(){
				var getPosition = this.getPosition();
				console.log(getPosition.lat(),getPosition.lng());	
				var lat = getPosition.lat();
				var lng = getPosition.lng();
			});
			
			if(fieldsType == 'input'){
				addValueToDb($('input#location_name').val(), lat, lng);
			}
			
          } else {
            alert('Geocode was not successful for the following reason: ' + status);
          }
        });
      }
      
	function addValueToDb(inputAddressVal, lat, lng){
		
		
		$.ajax({
			url:base_url+'map/add_location_details',
			type:'post',
			data:'inputAddressVal='+inputAddressVal+'&lat='+lat+'&lng='+lng,
			success:function(data){
				if(data == 'success'){
					getAllAddedLocations();
				}else{
					
				}
			}
		});
	}
	
	
	function getAllAddedLocations(){
		clearMarkers();
		
		$.ajax({
			url:base_url+'map/get_all_locations',
			type:'post',
			success:function(data){
				var data = JSON.parse(data);
				
				var html = '<table cellspacing="5" cellpadding="5" style="border:1px solid #dcdcdc" align="center">';
				for(var i = 0; i < data.length; i++){
					var myLatlng = new google.maps.LatLng(data[i].lat, data[i].lng); 
					marker = new google.maps.Marker({
						map: map,
						position:myLatlng
					});
					
					markersArr.push(marker);
					bounds.extend(myLatlng);
					
					html += '<tr id="trId_'+data[i].id+'"><td>'+data[i].location_address+'</td><td><button type="button" onclick="delete_location('+data[i].id+');" title="Click To Delete">Delete</button></td></tr>';
				}
				//map.fitBounds(bounds);
				
				html += '</table>';
				
				$('#locations_container').html(html);
			}
		});
	}
	
	function clearMarkers(){
		if(markersArr.length > 0){
			for(i in markersArr){
				markersArr[i].setMap(null);
			}
		}
	}
	function delete_location(delId){
		if(confirm('Do you really want to delete this record !!')){
			
			$.ajax({
				url:base_url+'map/delete_location',
				type:'post',
				data:'delId='+delId,
				success:function(data){
					if(data == 'success'){
						getAllAddedLocations();
					}else{
						
					}
				}
			});
		}else{
			
		}
	}
    </script>
  </head>
